func MyFunction()
{
  Log("My function has been called!");
}

func ShowNumber(int number)
{
  Log("ShowNumber has been called with parameter %d!", number);
}

func ShowSum(number1, number2, number3, number4)
{
  Log("The sum of the first four parameters is %d.", number1 + number2 + number3 + number4);
}

func TypeParameterFunction(object myClonk, id def, int count, string msg)
{
  for(var i = 0; i < count; i++)
    CreateContents(def, myClonk);
  myClonk->Message(msg);
}

func ShowDifference(number1, number2)
{
  Log("The difference between %d and %d is %d!", number1, number2, Difference(number1, number2));
}

private func PrivateFunction()
{
  // This function may only be called from the same script
}

global func CreateContents(id, obj, count)
{
  var obj;
  for(var i = 0; i < count; i++)
    obj = inherited(id, obj);
  return obj;
}

local number;
func ObjectFunction()
{
  Log("ObjectFunction: local number has the value %d!", number);
}

global func GlobalFunction()
{
  ObjectFunction(); // Error!
  number++; // Error!!
}
