func MeineFunktion()
{
  Log("Meine Funktion wurde aufgerufen!");
}

func ZeigeZahl(int zahl)
{
  Log("ZeigeZahl wurde mit dem Parameter %d aufgerufen!", zahl);
}

func ZeigeSumme(zahl1, zahl2, zahl3, zahl4)
{
  Log("Die Summe der ersten 4 Parameter ist %d.", zahl1 + zahl2 + zahl3 + zahl4);
}

func TypParameterFunktion(object Clonk, id def, int anzahl, string msg)
{
  for(var i = 0; i < anzahl; i++)
    CreateContents(def, Clonk);
  Clonk->Message(msg);
}

func ZeigeDifferenz(Zahl1, Zahl2)
{
  Log("Die Differenz zwischen %d und %d beträgt %d!", Zahl1, Zahl2, Differenz(Zahl1, Zahl2));
}

global func CreateContents(id, obj, count)
{
  var obj;
  for(var i = 0; i < count; i++)
    obj = inherited(id, obj);
  return(obj);
}

local number;
func ObjektFunktion()
{
  Log("ObjectFunktion: local number hat den Wert %d!", number);
}

global func GlobaleFunktion()
{
  ObjectFunktion(); // Fehler!
  number++; // Fehler!
}
