func ArraySum(array a)
{
  var l = GetLength(a);
  var result = 0;
  for (var i = 0; i < l; ++i)
  {
    result += a[i];
  }
  return (result);
}

func RandomID()
{
  var a = [Clonk, Wipf, Bird];
  return (a[Random(3)]);
}

func DetonateNearest()
{
  var a = FindObjects(Find_ID(Dynamite), Sort_Distance());
  for (var dyna in a[:6])
  {
    dyna->DoExplode();
  }
}
