example.code:
private func Burning()
{
  // Rauch
  Smoke(+23, Random(3) - 24, Random(5) + 8);
  // Noch nicht fertig
  if (GetActTime() < 150) return 1;
  // Fertig
  SetAction("Idle");
  // Erz suchen
  var ore;
  if (!(ore = FindContents(Ore))) return 1;
  // Erz verbrauchen
  ore->RemoveObject();
  // Metall auswerfen
  CreateContents(Metal)->Exit(-27, +13, 0, -1);
  // Sound
  Sound("Pshshsh");
}
