example.code:
/* Wird bei Doppelklick auf Graben aufgerufen */
protected func Activate()
{
	// Pfeil erstellen
	var arrow = CreateContents(Arrow);
	// X-Geschwindigkeit für den Pfeil ermitteln
	var xspeed = -10 + 20 * GetDir();
	// X-Ausgangsposition für den Pfeil ermitteln
	var xexit = -5 + 10 * GetDir();
	// Rotation des Pfeiles
	var rotation = -90 + 180 * GetDir();
	// Pfeil schleudern
	arrow->Exit(xexit, 0, rotation, xspeed, 0);
	return 1;
}
