example.code:
static num_scores;

protected func RemovePlayer(int player)
  {
  var player_id = GetPlayerID(player);
  Global(player_id*2)   = GetPlayerName(player);
  Global(player_id*2+1) = GetScore(player);
  ++num_scores;
  }

protected func OnGameOver()
  {
  var msg = "Scores:", i, plr_name;
  while (num_scores)
    if (plr_name = Global(i++*2))
      {
      msg = Format("%s|%s=%d", msg, plr_name, Global(i*2-1));
      --num_scores;
      }
  Message(msg);
  }
