example.code:
func FxIntCarryBowStart(object target, int number)
{
	EffectVar(0, target, number) = target->AttachMesh(Bow, "pos_hand1", "main");
	EffectVar(1, target, number) = true;
}

func FxIntCarryBowTimer(object target, int number)
{
	if(EffectVar(1, target, number))
		target->SetAttachBones(EffectVar(0, target, number), "pos_hand2", nil);
	else
		target->SetAttachBones(EffectVar(0, target, number), "pos_hand1", nil);
	EffectVar(1, target, number) = !EffectVar(1, target, number);
}
