example.code:
#include Clonk

local is_invisible;

protected func Recruitment(a,b,c,d,e,f,g,h,i,j)
  {
  var rVal = _inherited(a,b,c,d,e,f,g,h,i,j);
  if (GetCrewExtraData("C4ID_Invisible")) SetVisibility(VIS_Owner);
  return(rVal);
  }

protected func ControlSpecial()
  {
  if (is_invisible = !is_invisible)
    SetVisibility();
  else
    SetVisibility(VIS_Owner);
  SetCrewExtraData("C4ID_Invisible", is_invisible);
  }
