example.code:
private func GetOwnMass(clonk)
  {
  var mass=clonk->GetMass(), i, obj;
  while(obj = clonk->Contents(i++)) mass -= obj->GetMass();
  return(mass);
  }

protected func Activate(pByClonk)
  {
  [Essen]
  pByClonk->SetMass(GetOwnMass(pByClonk) + 100);
  pByClonk->Sound("ClonkMunch");
  return(RemoveObject());
  }
